mixin AyahLafaz implements Ayah {
  @protected
  @mustCallSuper
  List<Map<String, String>> getAyahLafaz(Location location) => [];
}

mixin FatihahAyahLafaz on AyahLafaz {
  static const List<List<Map<String, String>>> lafazs = <List<Map<String, String>>>[
    [ { 'بِسْمِ': 'dengan nama' }, { 'ٱللَّهِ': 'Allah' }, { 'ٱلرَّحْمَٰنِ': 'Maha Pengasih' }, { 'ٱلرَّحِيمِ': 'Maha Penyayang' } ],
    [ { 'ٱلْحَمْدُ': 'pujian' }, { 'لِلَّهِ': 'bagi Allah' }, { 'رَبِّ': 'Tuhan' }, { 'ٱلْعَٰلَمِينَ': 'alam semesta' } ],
    [ { 'ٱلرَّحْمَٰنِ': 'Maha Pengasih' }, { 'ٱلرَّحِيمِ': 'Maha Penyayang' } ],
    [ { 'مَٰلِكِ': 'Penguasa' }, { 'يَوْمِ': 'hari' }, { 'ٱلدِّينِ': 'pembalasan' } ],
    [ { 'إِيَّاكَ': 'hanya kepadaMu' }, { 'نَعْبُدُ': 'kami menyembah' }, { 'وَإِيَّاكَ': 'dan hanya kepadaMu' }, { 'نَسْتَعِينُ': 'kami mohon pertolongan' } ],
    [ { 'ٱهْدِنَا': 'tunjukkan kami' }, { 'ٱلصِّرَٰطَ': 'jalan' }, { 'ٱلْمُسْتَقِيمَ': 'lurus' } ],
    [ { 'صِرَٰطَ': 'jalan' }, { 'ٱلَّذِينَ': 'orang-orang yang' }, { 'أَنْعَمْتَ': 'diberi nikmat' }, { 'عَلَيْهِمْ': 'kepadanya' }, { 'غَيْرِ': 'bukan' }, { 'ٱلْمَغْضُوبِ': 'orang yang dimurkai' }, { 'وَلَا': 'dan tidak' }, { 'ٱلضَّآلِّينَ': 'orang-orang yang sesat' } ]
  ];

  @override
  List<Map<String, String>> getAyahLafaz(Location location) {
    if (location.surah == 1) {
      return lafazs[location.ayah - 1];
    }

    return super.getAyahLafaz(location);
  }
}

